rule.definition:
load(
  local!showDetails:false,
  local!pagingInfo:#"SYSTEM_SYSRULES_pagingInfo"(startIndex:1,batchSize:5,sort:#"SYSTEM_SYSRULES_sortInfo"(field:"expenseDateTime",ascending:false)),
  local!data:#"SYSTEM_SYSRULES_queryEntity"(
    entity:#"_a-0000dd17-9077-8000-9ba2-011c48011c48_10014",
    query:#"SYSTEM_SYSRULES_query"(
      pagingInfo:local!pagingInfo
    )
  ),
  local!dataAgg:#"SYSTEM_SYSRULES_queryEntity"(
    entity:#"_a-0000dd17-9077-8000-9ba2-011c48011c48_10014",
    query:#"SYSTEM_SYSRULES_query"(
      aggregation:#"SYSTEM_SYSRULES_queryAggregation"(
        aggregationColumns:{
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field:"type",alias:"type",isGrouping:true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field:"amount",alias:"amount",isGrouping:false,aggregationFunction:"SUM")
        }
      ),
      pagingInfo:#"SYSTEM_SYSRULES_pagingInfo"(startIndex:1,batchSize:-1)
    )
  ),
  local!tempReceipt:getdocumentsfromfolder(rootFolder:#"_a-0000dd17-9077-8000-9ba2-011c48011c48_9989",recursiveSearch:false)[1],
  #"SYSTEM_SYSRULES_dashboardLayout"(
    firstColumnContents:{
      /* Null check returning a banner notifying user that */
      /* they don't have any expenses associated with their account */
      if(#"_a-0000d62a-67b3-8000-da6f-010000010000_951"(local!data.data),
        #"0573d4fe-fcb5-4251-ad0e-081f618a4796"(type: "Expenses"),
        {
          #"SYSTEM_SYSRULES_sectionLayout"(
            firstColumnContents:{
              #"SYSTEM_SYSRULES_gridField"(
                totalCount:local!data.totalCount,
                columns:{
                  #"SYSTEM_SYSRULES_gridTextColumn"(
                    label:"Date",
                    field:"expenseDateTime",
                    data:index(local!data.data,"expenseDateTime",{})
                  ),
                  #"SYSTEM_SYSRULES_gridTextColumn"(
                    alignment:"RIGHT",
                    label:"Amount ($)",
                    field:"amount",
                    data:apply(fn!fixed(_,2),index(local!data.data,"amount",{}))
                  ),
                  #"SYSTEM_SYSRULES_gridTextColumn"(
                    label:"Type",
                    field:"type",
                    data:index(local!data.data,"type",{})
                  ),
                  #"SYSTEM_SYSRULES_gridTextColumn"(
                    label:"Comments",
                    field:"comments",
                    data:index(local!data.data,"comments",{})
                  ),              
                  #"SYSTEM_SYSRULES_gridImageColumn"(
                    label:"Receipt",
                    data:apply(#"SYSTEM_SYSRULES_documentImage"(document:_),repeat(count(index(local!data.data,"amount",{})),local!tempReceipt))
                  )           
                },
                value:local!pagingInfo,
                saveInto:{
                  local!pagingInfo,
                    a!save(local!data,#"SYSTEM_SYSRULES_queryEntity"(
                    entity:#"_a-0000dd17-9077-8000-9ba2-011c48011c48_10014",
                    query:#"SYSTEM_SYSRULES_query"(
                      pagingInfo:local!pagingInfo
                    )
                  ))
                }
              )
            }
          ),
          if(local!showDetails,
            #"SYSTEM_SYSRULES_sectionLayout"(
              firstColumnContents:{
                #"SYSTEM_SYSRULES_imageField"(
                  images:{
                    apply(#"SYSTEM_SYSRULES_documentImage"(document:_),tointeger(getdocumentsfromfolder(rootFolder:#"_a-0000dd17-9077-8000-9ba2-011c48011c48_9989",recursiveSearch:false)))
                  }
                )
              }
            ),
          {}),
          #"SYSTEM_SYSRULES_sectionLayout"(
            firstColumnContents:{
              #"SYSTEM_SYSRULES_pieChartField"(
                label:"Breakdown of expenses",
                showTooltips:true,
                showDataLabels:true,
                series:apply(#"SYSTEM_SYSRULES_chartSeries"(label:_,data:_),merge(local!dataAgg.data.type,local!dataAgg.data.amount))
              )
            }
          )
        }
      )
    }
  )
)
