rule.definition:
/* NOTE: currently passing in hardcoded userid TODO: null handling */
=load(
  local!selectedTrip,
  local!endPoints:{"40.736017,-74.173535"},
  local!comments,
  local!selectedRecentTripIdx,
  local!createNew:false,
  local!recentVehicles,
  local!recentLocations,
  #"SYSTEM_SYSRULES_formLayout"(
    firstColumnContents: {
      /*Displays Trip Summary Information*/
      #"51c9ee7e-d413-4fbb-80ac-73c5e29381c6"(
        startLocationId: ri!startLocationId,
        endLocationId: ri!endLocationId,
        selectedVehicleId: ri!selectedVehicleId,
        selectedRecentTrip: ri!selectedRecentTrip,
        recentLocations: local!recentLocations,
        recentVehicles: local!recentVehicles
      ),
      /*Prompts user to choose Existing or New trip*/
      if(or(local!createNew,not(isnull(ri!selectedRecentTrip))),
        {},
        #"abe97b64-adb3-44c1-ae8f-492a7cba079a"(
          selectedRecentTrip: ri!selectedRecentTrip,
          createNew: local!createNew,
          recentVehicles: local!recentVehicles,
          recentLocations: local!recentLocations
        )
      ),
      /*Prompts user to select a vehicle*/
      if(and(local!createNew,ri!selectedVehicleId<1),
        #"7c98133d-c841-4ff7-ab87-803e62aab0c1"(
          recentVehicles: local!recentVehicles,
          selectedVehicleId: ri!selectedVehicleId
        ),{}
      ),
      /*Prompts user to select a Start location*/
      if(and(local!createNew,ri!startLocationId<1),      
        #"60049f0e-bb06-4263-bc31-02bed043c9e0"(
          recentLocations: local!recentLocations,
          startLocationId: ri!startLocationId
        ),{}
      ),
      /*Prompts user to select an End location*/
      if(and(local!createNew,ri!endLocationId<1),
        #"8c29ccb3-91ab-422d-98c7-93cc1a8d6282"(
          recentLocations: local!recentLocations,
          endLocationId: ri!endLocationId
        ),{}
      ),
      /*Prompts user to enter Mileage info*/
      if(or(local!createNew,not(isnull(ri!selectedRecentTrip))),
        #"83216625-4c10-41a9-b646-2504a468113c"(
          createNew: local!createNew,
          odometer: ri!odometer,
          comments: local!comments,
          expenseFlag: ri!expenseFlag,
          endPoints: local!endPoints
        ),{}
      )
    },
    secondColumnContents: {},
    buttons: if(or(local!createNew,not(isnull(ri!selectedRecentTrip))),
      #"SYSTEM_SYSRULES_ButtonLayout"(
        secondaryButtons:{
            #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
              label: #"_a-0000dd17-9077-8000-9ba2-011c48011c48_6715",
              style: "NORMAL",
              value: #"_a-0000dd17-9077-8000-9ba2-011c48011c48_6715",
              skipValidation:true,
              saveInto: {
                  ri!submitValue
              }
            )          
        },
        primaryButtons: {
            #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
              label: if(ri!expenseFlag,#"_a-0000dd3a-49e4-8000-9ba2-011c48011c48_23606",#"_a-0000dd17-9077-8000-9ba2-011c48011c48_6617"),
              style: "PRIMARY",
              value:#"_a-0000dd17-9077-8000-9ba2-011c48011c48_6617",
              saveInto: {
                ri!submitValue,
                /* hard coded for now until we discuss user management strategy */
                a!save(ri!userId,1)
              }
            )
        }
      ),{}
    ),
    validations: {}
  )

)
